/* ==UserStyle==
@name			􀅓aidu
@author			Cascadea User
@version		1.0.0
@namespace		cascadea41492846
@preprocessor	stylus
==/UserStyle== */

@-moz-document domain("wenku.baidu.com")
{
	$pas='https://cdn.jsdelivr.net/gh/kyle1an/SF-@main';

	// $fontname='/Pro/SF-Pro-Text-';
	$fontname='/Compact/SF-Compact-Text-';

	$fondname='/Compact/SF-Compact-Text-';

	$fononame='/Mono/SF-Mono-';

	$ext='.otf';

	$uR=U+0000-024F, U+02B0-052F, U+1DC0-20CF, U+2190-22FF, U+2460-24FF, U+25A0-25FF, U+2700-27FF, U+2900-2BFF, U+2C60-2C7F, U+2DE0-2E7F, U+3200-32FF, U+A640-A69F, U+A700-A7FF, U+E000-F8FF, U+FFF0-FFFF, U+F0000-10FFFF;
	$u4=U+0000-024F, U+0250-02AF, U+02B0-052F, U+1D00-1DBF, U+1DC0-20CF, U+2100-218F, U+2190-22FF, U+2460-24FF, U+25A0-25FF, U+2700-27FF, U+2900-2BFF, U+2C60-2C7F, U+2DE0-2E7F, U+3200-32FF, U+A640-A69F, U+A700-A7FF, U+E000-F8FF, U+FFF0-FFFF, U+F0000-10FFFF;

	@font-face {
	    font-family: "微軟正黑體";
	    src: url($pas + $fontname + 'Regular' + $ext);
	    unicode-range: $u4;
	    font-weight: 400;
	}

	@font-face {
	    font-family: "微软雅黑";
	    src: url($pas + $fontname + 'Light' + $ext);
	    unicode-range: $uR;
	    font-weight: 300;
	}

	@font-face {
	    font-family: "微软雅黑";
	    src: url($pas + $fontname + 'LightItalic' + $ext);
	    unicode-range: $uR;
	    font-weight: 300;
	    font-style: italic;
	}

	@font-face {
	    font-family: "微软雅黑";
	    src: url($pas + $fontname + 'Regular' + $ext);
	    unicode-range: $u4;
	    font-weight: 400;
	}

	@font-face {
	    font-family: "微软雅黑";
	    src: url($pas + $fontname + 'RegularItalic' + $ext);
	    unicode-range: $u4;
	    font-weight: 400;
	    font-style: italic;
	}

	@font-face {
	    font-family: "微软雅黑";
	    src: url($pas + $fontname + 'Bold' + $ext);
	    unicode-range: $uR;
	    font-weight: 700;
	}

	@font-face {
	    font-family: "微软雅黑";
	    src: url($pas + $fontname + 'BoldItalic' + $ext);
	    unicode-range: $uR;
	    font-weight: 700;
	    font-style: italic;
	}

	@font-face {
	    font-family: "方正仿宋_gbk";
	    src: url($pas + $fontname + 'Regular' + $ext);
	    unicode-range: $u4;
	    font-weight: 400;
	}

	@font-face {
	    font-family: "方正仿宋_gbk";
	    src: url($pas + $fontname + 'RegularItalic' + $ext);
	    unicode-range: $u4;
	    font-weight: 400;
	    font-style: italic;
	}

	@font-face {
	    font-family: "方正仿宋_gbk";
	    src: url($pas + $fontname + 'Bold' + $ext);
	    unicode-range: $uR;
	    font-weight: 700;
	}

	@font-face {
	    font-family: "方正仿宋_gbk";
	    src: url($pas + $fontname + 'BoldItalic' + $ext);
	    unicode-range: $uR;
	    font-weight: 700;
	    font-style: italic;
	}

	@font-face {
	    font-family: "宋体";
	    src: url($pas + $fontname + 'Regular' + $ext);
	    unicode-range: $u4;
	    font-weight: 400;
	}

	@font-face {
	    font-family: "宋体";
	    src: url($pas + $fontname + 'RegularItalic' + $ext);
	    unicode-range: $u4;
	    font-weight: 400;
	    font-style: italic;
	}

	@font-face {
	    font-family: "宋体";
	    src: url($pas + $fontname + 'Bold' + $ext);
	    unicode-range: $uR;
	    font-weight: 700;
	}

	@font-face {
	    font-family: "宋体";
	    src: url($pas + $fontname + 'BoldItalic' + $ext);
	    unicode-range: $uR;
	    font-weight: 700;
	    font-style: italic;
	}
}

