@-moz-document domain("127.0.0.1"),
domain("135.64.138.163") {
    body {
        font-family: "Microsoft YaHei", Arial, Helvetica, sans-serif;
    }
    #assignStaffCode,
    #deleteStaffCode,
    #modStaffDate {
        display: inline-block!important;
    }
    .w800 {
        left: 40px;
        position: absolute;
    }
    a.dg {
        position: absolute;
        top: 45px;
        left: 0;
    }

    .more-qh {
        position: relative;
    }

    .more-qh a[value="4"] {
        position: absolute;
        left: 190px;
        top: -80px;
        color: red;
    }

    #menuPanelDiv.tab_bq {
        padding-left: 400px !important;
    }

    a[latnid="891"] {
        position: absolute;
        top: -40px;
        height: 40px;
        width: 95px;
    }

    a[latnid="891"],
    dt[latnid="891"] {
        color: red !important;
    }

    .indexSearch {
        position: absolute;
        left: 173px;
        top: 46px;
        z-index: 2;
    }

    a.icon-search img {
        height: 100px;
        width: 100px;
        opacity: 0.03;
    }

    /* 确定 */
    .layui-layer-btn0 {
        height: 80px !important;
        display: block !important;
    }

    /* 取消 */
    .layui-layer-btn1 {
        height: 50px !important;
        display: block !important;
    }

    .qrychooseAddrDIV input[type="checkbox"] {
        height: 30px;
        width: 30px;
    }

    [name="chooseAddrBtn"] {
        position: absolute;
        height: 190px;
    }

    [name="selectNbr"] {
        width: 400px;
    }

    .chooseAddrDIV.tcc-bg a[name="confirmChooseAddr"] {
        position: absolute;
        right: 100px;
        top: 40px;
        width: 60px;
        height: 90px;
    }
}

