/* ==UserStyle==
@name			-fontsDev
@author			Cascadea User
@version		1.0.0
@namespace		cascadea41492846
@preprocessor	less
==/UserStyle== */
@-moz-document regexp('(https?|chrome-extension):\\/\\/(localhost|127.0.0.1|172.16.1.213).*') {
.load(@textFamilies, @tarSF, @allWeights, @generalUnicode, false);
.load(@boldFamilies, @tarSF, @boldWeights, @generalUnicode, false);
.load(@monoFamilies, @tarMo, @usualWeights, @allUnicode);
.load(@compactFamilies, @tarCM, @allWeights, @generalUnicode);
.load-local(@zhFamilies, @tarCM, @allWeights, @zhUnicode, @styles);
@font-face {
	font-family: 'XcodeDigits';
	src: local('Xcode Digits Regular'), url("https://cdn.jsdelivr.net/gh/meewill/SF@main/Fonts/XcodeDigits-regular.ttf");
	unicode-range: U+0030-0039;
	font-weight: 400;
	font-style: normal;
	font-display: swap;
}

@zhFamilies: '宋体',
'Microsoft Yahei';
@textFamilies: 'SF Pro Text',
'宋体',
Arial,
'Helvetica Neue',
'SF Pro Display',
'PingFang SC',
'Microsoft Yahei';
@boldFamilies: 'Arial Black',
HelveticaNowDisplay-XBd;
@compactFamilies: 'SF Compact Text',
Mulish;
@monoFamilies: 'SF Mono',
'SFMono-Regular',
Ubuntu Mono;
@repo: 'https://cdn.jsdelivr.net/gh/meewill/SF@main';
@styles: {
	normal: '';
	italic: 'Italic';
}

@thinWeights: {
	Thin: 200;
}

@usualWeights: {
	Light: 300;
	Regular: 400;
	Medium: 500;
	Semibold: 600;
	Bold: 700;
}

@boldWeights: {
	Semibold: 600;
	Bold: 700;
}

@allWeights: {
	Thin: 200;
	Light: 300;
	Regular: 400;
	Medium: 500;
	Semibold: 600;
	Bold: 700
}

@tarSF: {
	nam: 'SF Pro Text';
	dir: '/Fonts/SF-Pro-Text-';
	ext: 'woff2';
}

@tarMo: {
	nam: 'SF Mono';
	dir: '/Fonts/SF-Mono-';
	ext: 'woff2';
}

@tarCM: {
	nam: 'SF Compact Text';
	dir: '/Fonts/SF-Compact-Text-';
	ext: 'woff2';
}

@formats: {
	@ttf: TrueType;
	@otf: OpenType;
	@woff: woff;
	@woff2: woff2;
}

.load(@families,
@tar,
@weights,
@unicode,
@local: true) {
	each(@families,
	.(@family, @k1, @i1) {
		each(@weights, .(@numeric, @weight-key, @i) {
			each(@styles, .(@style-val, @style, @i2) {
				// @keyword: extract(@weight, 1);
				// @numeric: extract(@weight, 2);
				@font-face {
					font-family: @family;
					.src(@family, @tar, @repo, @weight-key, @style-val, @numeric, @style, @local);
					unicode-range: @unicode;
					font-weight: @numeric;
					font-style: @style;
					font-display: swap;
				}
			}

			)
		}

		)
	}

	)
}

.src(@family,
@tar,
@repo,
@weight-key,
@style-val,
@numeric,
@style,
@local) {
	@nam: @tar[nam];
	@dir: @tar[dir];
	@ext: @tar[ext];
	@format: @formats[@@ext];
	@italic: if(@style-val='',
	e(''),
	' Italic');
	src: if(@local,
	local('@{nam} @{weight-key}@{italic}') e(','),
	) url('@{repo}@{dir}@{weight-key}@{style-val}.@{ext}') format('@{format}');
}

.load-local(@families,
@tar,
@weights,
@unicode,
@styles,
@local: true) {
	each(@families,
	.(@family,
	@k1,
	@i1) {
		@font-face {
			font-family: @family;
			src: local(@family);
			unicode-range: @unicode;
			font-display: swap;
		}
	}

	)
}

@generalUnicode: e('U+0000-024F'),
e('U+02B0-052F'),
e('U+1DC0-20CF'),
e('U+2190-22FF'),
e('U+2460-24FF'),
e('U+25A0-25FF'),
e('U+2700-27FF'),
e('U+2900-2BFF'),
e('U+2C60-2C7F'),
e('U+2DE0-2E7F'),
e('U+3200-32FF'),
e('U+A640-A69F'),
e('U+A700-A7FF'),
e('U+E000-F8FF'),
e('U+FFF0-FFFF'),
e('U+F0000-10FFFF');
@plusUnicode: e('U+0250-02AF'),
e('U+1D00-1DBF'),
e('U+2190-22FF');
@allUnicode: @generalUnicode,
@plusUnicode;
@zhUnicode: e('U+4E00-9FFF'),
e('U+3400-4DBF'),
e('U+20000-2A6DF'),
e('U+F900-FAFF'),
e('U+2F800-2FA1F'); // e('U+2A700–2B73F, U+2B740–2B81F, U+2B820–2CEAF'),

}

