}

.load-fonts(@monoFamilies,
@tarMo,
@monoWeights,
@allUnicode);
.load-fonts(@textFamilies,
@tarPT,
@allWeights,
@generalUnicode,
true);
.load-stretch-fonts(@textFamilies,
@tarCd,
@allWeights,
@generalUnicode,
true);
@textFamilies: 'SF Pro',
'Arial',
'Helvetica Neue',
'Helvetica';
@monoFamilies: 'SF Mono',
'SFMono-Regular',
'ui-monospace';
@repo: 'https://cdn.jsdelivr.net/gh/meewill/SF';
@styles: {
  normal: '';
  italic: 'Italic';
}

@thinWeights: {
  Thin: 200;
}

@usualWeights: {
  Light: 300;
  Regular: 400;
  Medium: 500;
  Semibold: 600;
  Bold: 700;
}

@boldWeights: {
  Semibold: 600;
  Bold: 700;
}

@monoWeights: {
  Thin: 200;
  Light: 300;
  Medium: 400;
  Medium: 500;
  Semibold: 600;
  Bold: 700;
  Heavy: 800;
}

@allWeights: {
  Thin: 200;
  Light: 300;
  Regular: 400;
  Medium: 500;
  Semibold: 600;
  Bold: 700;
  Heavy: 800;
  Black: 900;
}

@tarPT: {
  nam: 'SF Pro Text';
  dir: '/Fonts/SF-Pro-Text-';
  ext: 'woff2';
}

@tarMo: {
  nam: 'SF Mono';
  dir: '/Fonts/SF-Mono-';
  ext: 'woff2';
}

@tarCM: {
  nam: 'SF Compact Text';
  dir: '/Fonts/SF-Compact-Text-';
  ext: 'woff2';
}

@tarCd: {
  nam: 'SF Pro';
  dir: '/Fonts/SF-Pro';
  ext: 'woff2';
}

@formats: {
  @ttf: TrueType;
  @otf: OpenType;
  @woff: woff;
  @woff2: woff2;
}

.load-fonts(@families,
@tar,
@weights,
@unicode,
@local: false) {
  each(@families,
  .(@family, @k1, @i1) {
    each(@weights, .(@numeric, @weight-key, @i) {
      each(@styles, .(@style-val, @style, @i2) {
        // @keyword: extract(@weight, 1);
        // @numeric: extract(@weight, 2);
        @font-face {
          font-family: @family;
          .use-src(@family, @tar, @repo, @weight-key, @style-val, @numeric, @style, @local);
          unicode-range: @unicode;
          font-weight: @numeric;
          font-style: @style;
          font-display: swap;
        }
      }

      )
    }

    )
  }

  )
}

.use-src(@family,
@tar,
@repo,
@weight-key,
@style-val,
@numeric,
@style,
@local: false) {
  @nam: @tar[nam];
  @dir: @tar[dir];
  @ext: @tar[ext];
  @format: @formats[@@ext];
  @italic: if(@style-val='',
  e(''),
  ' Italic');
  src: url('@{repo}@{dir}@{weight-key}@{style-val}.@{ext}') format('@{format}');
}

.load-local-fonts(@families,
@tar,
@weights,
@unicode,
@styles,
@local: false) {
  each(@families,
  .(@family,
  @k1,
  @i1) {
    @font-face {
      font-family: @family;
      src: local(@family);
      unicode-range: @unicode;
      font-display: swap;
    }
  }

  )
}

.load-stretch-fonts(@families,
@tar,
@weights,
@unicode,
@local: false) {
  each(@families,
  .(@family,
  @k1,
  @i1) {
    each(@weights,
    .(@numeric, @weight-key, @i) {
      @font-face {
        font-family: @family;
        .use-stretch-src(@family, @tar, @repo, @weight-key, '', @numeric, '', @local);
        unicode-range: @unicode;
        font-weight: @numeric;
        font-stretch: condensed;
        font-display: swap;
      }
    }

    )
  }

  )
}

.use-stretch-src(@family,
@tar,
@repo,
@weight-key,
@style-val,
@numeric,
@style,
@local) {
  @nam: @tar[nam];
  @dir: @tar[dir];
  @ext: @tar[ext];
  @format: @formats[@@ext];
  src: if(@local,
  local('@{nam} Condensed @{weight-key}') e(','),
  ) url('@{repo}@{dir}.@{ext}') format('@{format}');
}

@generalUnicode: e('U+0000-024F'),
